class GitHubCards extends Component {
  Title = [];
  state = {
    repo: []
  };

    componentDidMount() {
      const api_key = process.env.REACT_APP_API_KEY;
     
      

  
      let repo = [
              baseURL(`CMST488-AdvancedJS`),
              baseURL(`Amol-React-Portfolio`),
              baseURL(`PapaRebuild`),
              baseURL(`CMST388`),
              baseURL(`AmolPortfolio`)
            ];
        
  repo.map(
          (url) =>
             Axios.get(url, {
              headers: {
                Authorization: `token ${api_key}`,
              },
            }).then( (res) => {
              this.setState({
                repo: [...this.state.repo, res.data],
              });
            })
        );

  
}
render() {
 
  const { repo } = this.state;
  return (
    


    <Grid container spacing={5} >
      {repo.map((data, i) => (
        <RepoCard repo={data} key={i}  />
      ))}
    </Grid>
  );

}
}


export default GitHubCards;
