import React from "react";
import Axios from "axios";
import "../index.css";
import {useState} from "react";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faExternalLinkAlt } from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs, faHtml5, faCss3Alt } from "@fortawesome/free-brands-svg-icons";
import { Link } from "react-router-dom";






const getAllProjects = () => {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
  
    useEffect(() => {
      Axios.get("http://localhost:3000/projects.json")
        .then((response) => {
          setProjects(response.data);
          setLoading(false);
        })
        .catch((error) => {
          setError(error);
          setLoading(false);
        });
    }, getProject());
  
    return { projects, loading, error };
    };

    const getProject = () => { 
        const [project, setProject] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);
        const { id } =  useParams();

        useEffect(() => {
            Axios.get(`http://localhost:3000/projects/${id}.json`)
            .then((response) => {
                setProject(response.data);
                setLoading(false);
            })
            .catch((error) => {
                setError(error);
                setLoading(false);
            });
        }, [id]);
        return { project, loading, error };
    };


    export default function ProjectHoverCard() {

    const { projects, loading, error } = getAllProjects();

    // set state for hover 
    const [show, setShow] = useState(false);

    // set state for hover one at a time

    const onMouseEnterHandler = () => {
        setShow(true);
    };

    const onMouseLeaveHandler = () => {
        setShow(false);
    };
    




    return (
        <div className="project-grid">
            {projects.map((project) => (
                
                <div className="project-hover-card__container" key={project.id}>
                    <div className="project-hover-card__container__image">
                        <img src={project.image} alt={project.title} 
                        onMouseEnter={() => {
                            onMouseEnterHandler();
                            }}
                          onMouseLeave={() => {
                            onMouseLeaveHandler();
                          }}
                        />
                    </div>

                    {show && (
                    <div className="project-hover-card__container__overlay">
                        <div className="project-hover-card__container__overlay__content">
                            <h2>{project.title}</h2>
                            <p>{project.description}</p>
                            <div className="project-hover-card__container__overlay__content__icons">
                              
                                <a href={project.link
                                } target="_blank" rel="noreferrer">
                                    <FontAwesomeIcon icon={faExternalLinkAlt} />
                                </a>
                            </div>
                            <div className="project-hover-card__container__overlay__content__tech">
                                <FontAwesomeIcon icon={faReact} />
                                <FontAwesomeIcon icon={faJs} />
                                <FontAwesomeIcon icon={faHtml5} />
                                <FontAwesomeIcon icon={faCss3Alt} />

                            </div>
                        </div>
                    </div>
                    )}
                </div>
            ))}
        </div>
    );
}
