import React from "react";
import Axios from "axios";
import "../index.css";
import {useState} from "react";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faExternalLinkAlt } from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs, faHtml5, faCss3Alt } from "@fortawesome/free-brands-svg-icons";
import { Link } from "react-router-dom";






const getAllProjects = () => {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
  
    useEffect(() => {
      Axios.get("http://localhost:3000/projects.json")
        .then((response) => {
          setProjects(response.data);
          setLoading(false);
        })
        .catch((error) => {
          setError(error);
          setLoading(false);
        });
    }, []);
  
    return { projects, loading, error };
    };

 const mouseDown = () => {
    const [projectOverlay, setProjectOverlay] = useState(false);
    const [project, setProject] = useState(null);
    const { id } = useParams();
    const { projects, loading, error } = getAllProjects();

    useEffect(() => {
        if (id) {
            const project = projects.find((project) => project.id === id);
            setProject(project);
        }
    }, [id, projects]);

    return (
        <div className="project-overlay">
            <div className="project-overlay-content">
                <div className="project-overlay-header">
                    <h2>{project.title}</h2>
                    <div className="project-overlay-icons">
                       
                        <a href={project.link} target="_blank" rel="noreferrer">
                            <FontAwesomeIcon icon={faExternalLinkAlt} />
                        </a>
                    </div>
                </div>
                </div>
                </div>
                );
                };


       

    
   

    export default function ProjectHoverCard() {
    const { projects} = getAllProjects();
  
   

  
    return (
      <div className="project-grid">
        <div className="home-project-card">
          <h2>Recent Projects</h2>
        </div>
        {projects.map((project) => (
          <div className="home-project-card">
            
              <img src={project.image} alt={project.title}
              onMouseDown={() => {
                mouseDown();
                }}
              />
           
            
          </div>
        ))}
        </div>
    );
    }
