import React from 'react';
import '../index.css';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';


import { Axios } from 'axios';
import {useState }from 'react';





// projectgrid that gets data from each project in projects.js and displays it




// projectgrid that gets data from each project in projects.js and displays it






  

function ProjectGrid() {
  const [project, setProject] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);


  React.useEffect(() => {
    const fetchData = async () => {
      try {
        setError(null);
        setProject(null);
        setLoading(true);
        const response = await Axios.get(
          api/projects.json
        ).then((response) => {
          console
            .log(response)
            .catch((error) => {
              console.log(error);
            });
        });
        setProject(response.data);
      } catch (e) {
        setError(e);
      }
      setLoading(false);
    };
    fetchData();
  }, []);

  if (loading) return <div>loading...</div>;
  if (error) return <div>an error has occurred</div>;
  if (!project) return null;

  return (




 <div className='project-grid'>
      <div className='home-project-card'>
        <h2>Recent Projects</h2>
      </div>
      <Box>
        <div className='home-project-card'>
          <div className='container'>
            <h3></h3>
            <div className='card-body'>
              <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla
                facilisi. Curabitur venenatis, nulla id tincidunt ultrices, quam
                metus tincidunt lacus, eu posuere nisi lectus at nulla.
              </p>
              <img src={require("../assests/uniken.png")} alt="Your Name" />
            </div>
            <div className='double-button'>
              <Button variant="contained" color="primary" size='small'> Contact Me</Button>
              <Button variant="outlined" color="primary" size='small' > Contact Me</Button>
            </div>
          </div>
        </div>
      </Box>
     </div>

  );

}



