import React from "react";
import Axios from "axios";
import "../index.css";
import {useState} from "react";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faExternalLinkAlt } from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs, faHtml5, faCss3Alt } from "@fortawesome/free-brands-svg-icons";
import { Link } from "react-router-dom";


class ProjectCardHover extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            project: { title: "", description: "", image: "", link: "", github: "", technologies: [] }


        }
    }


    componentDidMount() {
        Axios.get("http://localhost:8080/projects/" + this.props.match.params.id)
            .then((response) => {
                this.setState({ project: response.data })
            })
            .catch((error) => {
                console.log(error);
            })
    }





    render() {
        return (
            <div className="project-card">
                <div className="project-card__header">
                    <h1 className="project-card__title">{this.state.project.title}</h1>
                    <p className="project-card__description">{this.state.project.description}</p>
                </div>
                <div className="project-card__body">
                    <div className="project-card__body__image">
                        <img src={this.state.project.image} alt="project" />
                    </div>
                    <div className="project-card__body__links">
                        <p className="project-card__body__links__title">Links</p>
                        <div className="project-card__body__links__container">
                           
                            <div className="project-card__body__links__container__icon">
                                <FontAwesomeIcon icon={faExternalLinkAlt} size="2x" />
                            </div>
                        </div>
                    </div>
                </div>
                <div className="project-card__footer">
                    <p className="project-card__footer__title">Technologies</p>
                    <div className="project-card__footer__container">
                        <div className="project-card__footer__container__icon">
                            <FontAwesomeIcon icon={faReact} size="2x" />
                        </div>
                        <div className="project-card__footer__container__icon">
                            <FontAwesomeIcon icon={faJs} size="2x" />
                        </div>
                        <div className="project-card__footer__container__icon">
                            <FontAwesomeIcon icon={faHtml5} size="2x" />
                        </div>
                        <div className="project-card__footer__container__icon">
                            <FontAwesomeIcon icon={faCss3Alt} size="2x" />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
                       
}



export default ProjectCardHover;
