 // create a hero component that includes the header, about me, and button to view work. Then import the hero component into the landing page and render it.

 import * as React from 'react';
 import Avatar from '@mui/material/Avatar';
 import Button from '@mui/material/Button';
 import CssBaseline from '@mui/material/CssBaseline';
 import TextField from '@mui/material/TextField';
 import FormControlLabel from '@mui/material/FormControlLabel';
 import Checkbox from '@mui/material/Checkbox';
 import Link from '@mui/material/Link';
 import Paper from '@mui/material/Paper';
 import Box from '@mui/material/Box';
 import Grid from '@mui/material/Grid';
 import ButtonGroup from '@mui/material/ButtonGroup';
 
 import Typography from '@mui/material/Typography';
 import { createTheme, ThemeProvider } from '@mui/material/styles';
 
import '../index.css';
import { Container} from '@mui/system';


    function Hero() {
        return (
            
                <Box height='100vh' width='100%' 
                sx={{
                    backgroundImage: "url('/assests/bgtest.jpeg')",
                    backgroundClip: "content-box",
                    backgroundSize: "cover",
                    backgroundPosition: "center",
                    backgroundRepeat: "no-repeat",
                    }} >
                        <Box height='100vh' justifyContent='center' alignItems='center' display='flex'
                        flexDirection='column' sx={{
 backdropFilter: "blur(32px)",
 padding: "10px",

                        }}>
                            <Typography textAlign='center' variant="h1"  fontWeight='800' component="div" >
                               Welcome to Amol's Portfolio
                            </Typography>
                            <Typography textAlign='center' variant="h6" gutterBottom component="div" >
                                Full Stack Web Developer and Enthuiest of all things tech.
                            </Typography>
                            <Typography variant='button'>
                                <ButtonGroup variant='contained' component="div"  aria-label="contained primary button group">
                                    <Button  color='primary'>View Work</Button>
                                    <Button>Contact Me</Button>
                                </ButtonGroup>
                            </Typography>
                        </Box>
                    </Box>
      


        );
    }

    export default Hero;
