import React from "react";
import "../index.css";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import { styled } from "@mui/material/styles";
import Card from "@mui/material/Card";
import { useParams } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFacebook, faTwitter, faLinkedin, faGithub, faInstagram, faYoutube } from "@fortawesome/free-brands-svg-icons";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import { faFile } from "@fortawesome/free-solid-svg-icons";
import { faFilePdf } from "@fortawesome/free-solid-svg-icons";
import {  faExternalLinkAlt } from "@fortawesome/free-solid-svg-icons";
import { useEffect } from "react";
import { Link } from "react-router-dom";
import  Axios  from "axios";
import { useState } from "react";



const Item = styled(Card)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
  margin: "0 auto",
  width: "100%",
  maxWidth: "100%",
  minWidth: "100%",
  height: "100%",
  maxHeight: "100%",
  minHeight: "100%",
  borderRadius: "0",
  backgroundColor: "#0f0f0f",
  boxShadow: "none",
  border: "none",
  display: "flex",
  flexDirection: "column",
  justifyContent: "space-between"
}));

const getProject = ( ) => {
  const  [id ]= useState(useParams().id);
  const [project, setProject] = useState({});

 useEffect(() => {
    Axios.get(`http://localhost:3000/projects.json?id=${id}`)
      .then((res) => {
        setProject(res.data);
      })
      .catch(err => {
        console.log(err);
      });
  }, [id]);

  return { project  };
};

function Project(){
  const { project} = getProject();
  return (
    <Box>
      <Item>
        <div className="project-detail">
          <div className="project-detail__content">
            <div className="project-detail__content__title">
              <h1>{project.title}</h1>
              <h4>{project.description}</h4>
            </div>
            <div className="project-detail__content__image">
              <img src={project.image} alt="" />
            </div>
            <div className="project-detail__content__info">
              <p>{project.content}</p>
            </div>
            <div className="project-detail__content__link">
              <ul>
                <li>
                  <a href={project.github} target="_blank" rel="noreferrer">
                    <FontAwesomeIcon icon={faGithub} size="2x" />
                  </a>
                </li>
                <li>
                  <a href={project.live} target="_blank" rel="noreferrer">
                    <FontAwesomeIcon icon={faExternalLinkAlt} size="2x" />
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </Item>
    </Box>
  );

}

export default Project;
