import React from "react";
import Axios from "axios";
import "../index.css";
import {useState} from "react";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faExternalLinkAlt } from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs, faHtml5, faCss3Alt } from "@fortawesome/free-brands-svg-icons";
import { Link } from "react-router-dom";
import { AnimationWrapper } from "react-hover-animation";
import { Grow, Box } from '@mui/material'; 
import Switch from '@mui/material/Switch';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormGroup from '@mui/material/FormGroup';
import { styled } from '@mui/material/styles';
import { useSpring, animated } from 'react-spring';
import { useHover } from 'react-use-gesture';








const getAllProjects = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    Axios.get("http://localhost:3000/projects.json")
      .then((response) => {
        setProjects(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  return { projects, loading, error };
};




function ProjectPage() {
  const { projects, loading, error } = getAllProjects();
  const [isHovering, setIsHovering] = useState(false);

  const [hoveredProject, setHoveredProject] = useState(null);


  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error!</p>;

  const handleMouseOver = (id) => {



    setIsHovering(true);
    setHoveredProject(id);
   
  };

  const handleMouseOut = () => {
    setIsHovering(false);
    setHoveredProject(null);

  };




  return (
    <div className="project-grid">
    
      {projects.map((project) => (
      <header >
      
      <div
        className='home-project-card-2 image'
        style={{ backgroundImage:`url( ${project.image})`  }}
        
        onMouseOver={() => handleMouseOver(project.id)}
        onMouseOut={() => handleMouseOut()}
        control={<Switch checked={checked} onChange={handleChange} />}
      >
  
        { isHovering && hoveredProject === project.id && 
       <Box sx={{ display: 'flex' }}>
       <Grow in={checked}>
        
       <div className='mask' style={{ backgroundColor: 'rgba(0, 0, 0, 0.8)' }}>
        
        <div className='project-card-content '>
        <h3>{project.title}</h3>

        <p> {project.description} </p>
         
         <div className="project-card-links container">
           <Link to={`/project-detail/${project.id}`}> 
           <FontAwesomeIcon icon={faExternalLinkAlt} />
           </Link>
           <a href={project.link} target="_blank">
             <FontAwesomeIcon icon={faExternalLinkAlt} />
           </a>
         </div>
        </div>
       
        </div>
       
       </Grow>
       {/* Conditionally applies the timeout prop to change the entry speed. */}
       <Grow
         in={checked}
         style={{ transformOrigin: '0 0 0' }}
         {...(checked ? { timeout: 1000 } : {})}
       >
         {icon}
       </Grow>
     </Box>
   
        }
      </div>
    
    </header>
            ))}
            </div>
  );

}

export default ProjectPage;
