import React from "react";
import "../index.css";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import { styled } from "@mui/material/styles";
import Card from "@mui/material/Card";
import { useParams } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFacebook, faTwitter, faLinkedin, faGithub, faInstagram, faYoutube } from "@fortawesome/free-brands-svg-icons";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import { faFile } from "@fortawesome/free-solid-svg-icons";
import { faFilePdf } from "@fortawesome/free-solid-svg-icons";
import {  faExternalLinkAlt } from "@fortawesome/free-solid-svg-icons";
import { useEffect } from "react";
import { Link } from "react-router-dom";
import  Axios  from "axios";
import { Container } from "@mui/system";
import { useState } from "react";
import SocialBar from "../components/social-bar";
import Paper from "@mui/material/Paper";
import { Typography } from "@mui/material";



const getProjectById = () => {
  const [project, setProject] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    Axios.get("http://localhost:3000/projects.json")
      .then((response) => {
        setProject(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  return { project, loading, error };
};

function ProjectDetail(props) {
  const { project, loading, error } = getProjectById();
  const { id } = useParams();
  const projectById = { ...project[id - 1]}
  return (
    <div class="wrapper">
     
        <Container maxWidth='xl'>
          <Paper sx={{
            backgroundColor: "#1a1a1a",
            color: "white",
            padding: "0em",
            gap: "2em",
          }}>
            <div className="project-detail-content">
          
                    <img  src={projectById.image} alt={projectById.title} />
          
                  <div className="project-detail-text">
                    
                    <div>
                      <Typography variant='h3'>
                        {projectById.title}
                      </Typography>
                    </div>
                    <div>
                      <Typography  variant="body1" fontSize={20} paragraph>{projectById.description}</Typography>
                    </div>
                  </div>
                  <div className="project-detail-links">
                    <h3> Made with:</h3>
                    <a href={projectById.link} target="_blank">
                      <FontAwesomeIcon color="white" icon={faExternalLinkAlt} />
                    </a>
                    <a href={projectById.github} target="_blank">
                      <FontAwesomeIcon icon={faGithub} />
                    </a>
                  </div>
                </div>
          </Paper>
        </Container>
   
   
    </div>
  );
}

export default ProjectDetail;
