 // create a hero component that includes the header, about me, and button to view work. Then import the hero component into the landing page and render it.

 import * as React from 'react';
 import Avatar from '@mui/material/Avatar';
 import Button from '@mui/material/Button';
 import CssBaseline from '@mui/material/CssBaseline';
 import TextField from '@mui/material/TextField';
 import FormControlLabel from '@mui/material/FormControlLabel';
 import Checkbox from '@mui/material/Checkbox';
 import Link from '@mui/material/Link';
 import Paper from '@mui/material/Paper';
 import Box from '@mui/material/Box';
 import Grid from '@mui/material/Grid';
 
 import Typography from '@mui/material/Typography';
 import { createTheme, ThemeProvider } from '@mui/material/styles';
 
import '../index.css';
import { Container } from '@mui/system';


    function Hero() {
        return (
            <Container maxWidth="xl">

         <Paper 
             sx={{
                p: '1em',
                display: 'flex',
                flexDirection: 'column',
                gap: 1,
                alignItems: 'center',
                justifyContent: 'center',
                width: 'auto',
                margin: ' 0 auto',
                backgroundColor: '#1e1e1e',
                color: '#ffffff',
                overrides: {
                    MuiInputLabel: {
                        root: {
                            color: "rgba(255, 255, 255, 0.87)",
                        },
                    },
                },
            }}>
             <div className='wrapper'>
                 <div className='hero'>
                   
                        <Box sx={{
                            typography: 'h5',
                            backgroundImage : 'url(../assests/profilepic.png)',
                            backgroundSize: 'cover',
                            backgroundPosition: 'center',
                            backgroundRepeat: 'no-repeat',
                            backgroundClip: 'content-box',
                            justifyContent: 'center',
                        
                           
                            
                           
                        }}>
                            <Container sx={{
                                backdropFilter: 'blur(8px)',
                                justifyContent: 'center',
                            }}>
                                <Typography sx={{
                                
                                     
                                }} variant='h2' textAlign='center' justifyContent='center'>
                                <h1 className='name'>Welcome to my portfolio</h1>
                                  </Typography>
                                <Typography sx={{
                                     
                                }} variant='subtitle1' paragraph  textAlign='center'>
                                    <p className='bio'>
                                        A highly skilled and motivated web developer with experience
                                        in creating custom websites using CMS platforms, as well as troubleshooting and maintaining servers. Proficient in various programming languages and operating systems, with strong problem-solving and communication skills.
                                    </p>
                                </Typography>
                                <Box sx={{
                                    justifyContent: 'center',
                                }}><Button  variant="contained" href="/projectpage">View My Work</Button></Box>
                                {/* button to go to projects page */}
                            </Container>
                        </Box>
                       
                    </div>
             </div>
         </Paper>
            </Container>
     );
        }

    export default Hero;
