import React from "react";
import Axios from "axios";
import "../index.css";
import {useState} from "react";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faExternalLinkAlt, faFirstAid } from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs, faHtml5, faCss3Alt } from "@fortawesome/free-brands-svg-icons";
import { Link } from "react-router-dom";
import { toUnitless } from "@mui/material/styles/cssUtils";








const getAllProjects = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    Axios.get("http://localhost:3000/projects.json")
      .then((response) => {
        setProjects(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  return { projects, loading, error };
};





function ProjectPage() {
  //  load each project from projects.json and display each image on a grid. 
  //  when a user hovers over the image, the title, description, and links will appear over the image. 
  //when the user leaves the hover, the text should disappear.

 
  const { projects, loading, error } = getAllProjects();
  const { id } = useParams();
  const project = projects.find((project) => project.id === id);

  return (
    <div className="project-page">
      <div className="project-page-content">
        <div className="project-page-image" 
        >
          
          <img src={project.image} alt={project.title} />
          
   
    
        </div>
        <div className="project-page-text">
          <h2>{project.title}</h2>
          <p>{project.description}</p>
          <div className="project-page-links">
            

            </div>
          </div>
        </div>
      </div>
    );



}

export default ProjectPage;
