import React from "react";
import Axios from "axios";
import "../index.css";
import {useState} from "react";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faExternalLinkAlt } from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs, faHtml5, faCss3Alt } from "@fortawesome/free-brands-svg-icons";
import { Link } from "react-router-dom";






const getAllProjects = () => {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
  
    useEffect(() => {
      Axios.get("http://localhost:3000/projects.json")
        .then((response) => {
          setProjects(response.data);
          setLoading(false);
        })
        .catch((error) => {
          setError(error);
          setLoading(false);
        });
    }, []);
  
    return { projects, loading, error };
    };

   
 export default function ProjectHoverCard() {

    const { projects, loading, error } = getAllProjects();
    






    const [hover, setHover] = useState(false);
   


    const handleMouseOver = (id) => {
        setState({hover: true}); //when mouse is over, set state to true
        setProject(projects.find((project) => project.id === id));


    }

    const handleMouseOut = () => {

        setState({hover: false}); //when mouse is out, set state to false

    }

    
    return (
        <div className="project-grid">
            <div className="home-project-card">
                <h2>Recent Projects</h2>
            </div>


       
        {projects.map((project) => (

            <div className="home-project-card" key={project.id}>
                <div className="project-card-image">
                    <img src={project.image} alt={project.title} 
                    onMouseOver={() => handleMouseOver(project.id)}
                    onMouseOut={() => handleMouseOut()}


                    />
                </div>

                {state.hover && project.id === project.id &&
                (
                    <div className="project-card-overlay">
                        <h3>{project.title}</h3>
                        <p>{project.description}</p>
                        <div className="project-card-icons">
       
                            <a href={project.link } target="_blank" rel="noreferrer"><FontAwesomeIcon icon={faExternalLinkAlt} /></a>
                        </div>
                        <div className="project-card-tech">
                            <FontAwesomeIcon icon={faReact} />
                            <FontAwesomeIcon icon={faJs} />
                            <FontAwesomeIcon icon={faHtml5} />
                            <FontAwesomeIcon icon={faCss3Alt} />
                        </div>
                    </div>
                ) 
                }
            </div>

            
        ))}
        </div>
    );
};
