import React from "react";
import Axios from "axios";
import "../index.css";
import {useState} from "react";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faExternalLinkAlt } from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs, faHtml5, faCss3Alt } from "@fortawesome/free-brands-svg-icons";
import { Link } from "react-router-dom";






const getAllProjects = () => {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
  
    useEffect(() => {
      Axios.get("http://localhost:3000/projects.json")
        .then((response) => {
          setProjects(response.data);
          setLoading(false);
        })
        .catch((error) => {
          setError(error);
          setLoading(false);
        });
    }, []);
  
    return { projects, loading, error };
    };

    
   

    export default function ProjectHoverCard() {
    const { projects, loading, error } = getAllProjects();
    const [project, setProject] = useState(null);
    const [isHovering, setIsHovering] = useState(false);

    const mouseEnter = (e) => {
        e.target.setIsHovering(true);
        setProject(project);
    }

   const mouseLeave = () => {
        setIsHovering(false);
        setProject(null);
    }

        return (




<div className="project-grid">
      <div className="home-project-card">
        <h2>Recent Projects</h2>
      </div>
      {projects.map((project) => (
        <div className="home-project-card">
          
            <img src={project.image} alt={project.title}
      
            onMouseEnter = {mouseEnter}
            onMouseLeave={mouseLeave}

            
            />

            {isHovering && ( 
                <div className="project-hover-card">
                    <h3>{project.title}</h3>
                    <p>{project.description}</p>
                    </div>
            )}
            </div>
        ))}

        </div>
        );
    }
