import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Container from '@mui/material/Container';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import MiniContact from '../components/mini-contact';
import SocialBar from '../components/social-bar';
import theme from '../pages/theme';
import { Grid, Hidden } from '@mui/material';


export default function ContactPage(){
    const styles = {
        floatingLabelFocusStyle: {
            color: "white"
        }
    }


    

    

return(
// dark mode form with first name, last name, email, message fields and a blue submit button

    
    
 
        
      
<Grid container maxWidth='lg' sx={{
    
    
    height: '100vh',
    padding: 2,


}} >
    
                <Grid item xs={12} md={12} lg={4} xl={4}> 
                    <Paper
                        component="form"
                        sx={{
                     height: '100%',
                     width: '100%',
                     
                            p: '1em',
                            display: 'flex',
                            flexDirection: 'column',
                            gap: 1,
                            alignItems: 'center',
                            justifyContent: 'center',
                           
                            margin: ' 0 auto',
                            backgroundColor: (theme.palette.primary.main),
                            color: '#ffffff',
                            overrides: {
                                MuiInputLabel: {
                                    root: {
                                        color: "rgba(255, 255, 255, 0.87)",
                                    },
                                },
                            },
                        }}
                        elevation={24}
                    >
                        <h1 style={{ color: '#ffffff' }}>Contact Me</h1>
                        </Paper>
                </Grid>
                    <Grid item xs={12} md={12} lg={8} xl={8} sx={{

                      
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                        backgroundColor: (theme.palette.primary.bg),
                    }}>
                        <Paper elevation={24} >
                            <TextField
                                sx={{ m: 1, color: '#ffffff',  }}
                                id="outlined-basic"
                                label="First Name"
                                variant="filled"
                                color="primary"
                                className='text-field'
                                InputLabelProps={{ style: { color: '#ffffff' } }} />
                            <TextField
                                sx={{ m: 1,  }}
                                id="outlined-basic"
                                label="Last Name"
                                variant="filled"
                                color="primary"
                                InputLabelProps={{ style: { color: '#ffffff' } }} />
                            <TextField
                                sx={{ m: 1, }}
                                id="outlined-basic"
                                label="Email"
                                variant='filled'
                                color="primary"
                                InputLabelProps={{ style: { color: '#ffffff' } }} />
                            <TextField
                                sx={{ m: 1, }}
                                id="outlined-basic"
                                label="Message"
                                variant="filled"
                                color="primary"
                                multiline
                                InputLabelProps={{ style: { color: '#ffffff' } }} />
                            <Button
                                sx={{ m: 1,  }}
                                variant="outlined"
                                color="primary"
                            >
                                Submit
                            </Button>
                        </Paper>
                    </Grid>
                    <Box sx={ {
    
                        marginTop: '1em'
    
                    }}>
    
    
            </Box>
              
</Grid>



          
    
        
        
     
   

);

}
