import React, { useState } from "react";
import Axios from "axios";
import "../index.css";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faExternalLinkAlt } from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs, faHtml5, faCss3Alt } from "@fortawesome/free-brands-svg-icons";
import { Link } from "react-router-dom";
import { render } from "@testing-library/react";
import { faGithubSquare } from "@fortawesome/free-brands-svg-icons";

function Project(props) {

  const { val, index, hover, setHover, apps, setApps } = props;
  const { id } = useParams();
  

  const [project, setProject] = useState(null);

  useEffect(() => {
    Axios.get("http://localhost:3000/projects.json").then((response) => {
      setProject(response.data);
    });
  }, []);

  return (
    <div className="project-card">
      <div className="project-card-inner">
        <div className="card-body">
          <img src={val.image}
          onMouseEnter={() => setHover(index)}
          onMouseLeave={() => setHover(null)}

          alt="project" />
        </div>
        <div className="project-card-back">
          <h1>{val.name}</h1>
          <ul>
            <li>
              <FontAwesomeIcon icon={faReact} />
            </li>
            <li>
              <FontAwesomeIcon icon={faJs} />
            </li>
            <li>
              <FontAwesomeIcon icon={faHtml5} />
            </li>
            <li>
              <FontAwesomeIcon icon={faCss3Alt} />
            </li>
          </ul>
          <div className="project-links">
          
            <a href={val.website} target="_blank" rel="noreferrer">
              <FontAwesomeIcon icon={faExternalLinkAlt} />
            </a>
          </div>
        </div>
      </div>
    </div>
  );
   

}

const ProjectHoverCard = (props) => {
  const [projects, setProjects] = useState([]);
  const [hover, setHover] = useState(null);
  const { id } = useParams();
  const [apps, setApps] = useState([]);
    

  useEffect(() => {
    Axios.get("http://localhost:3000/projects.json").then((response) => {
      setProjects(response.data);
    });
  }, []);



  return (
    <div className="wrapper">
      <div className="project-grid">
        {projects.map((val, index) => {
          return(
          <div>
       <Project key={index} val={val} index={index} hover={hover} setHover={setHover} apps={apps} setApps={setApps} />
        </div>
          )
        })}
      </div>
    </div>

  );


 
};

export default ProjectHoverCard;
