import React from "react";
import Axios from "axios";
import "../index.css";
import {useState} from "react";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faExternalLinkAlt } from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs, faHtml5, faCss3Alt } from "@fortawesome/free-brands-svg-icons";
import { Link } from "react-router-dom";

// get all the projects from the database and display only the images, until you hover over them and can see the rest of the details

const ProjectHoverCard = () => {
  const [projects, setProjects] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    Axios.get("http://localhost:3000/projects.json").then((response) => {
      setProjects(response.data);
    });
  }, []);

  return (
    <div className="container">
      <div className="row">
        {projects.map((val) => {
          return (
            <div className="col-12 col-md-6 col-lg-4">
              <div className="card">
                <div className="img-container">
                  <img src={val.image} alt="" />
                </div>
                <div className="card-body">
                  <h3>{val.title}</h3>
                  <p>{val.description}</p>
                  <div className="icons">
                    <div className="icon">
                      <FontAwesomeIcon icon={faGithub} />
                    </div>
                    <div className="icon">
                      <FontAwesomeIcon icon={faExternalLinkAlt} />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ProjectHoverCard;
