import React from "react";
import Axios from "axios";
import "../index.css";
import {useState} from "react";
import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub, faExternalLinkAlt } from "@fortawesome/free-solid-svg-icons";
import { faReact, faJs, faHtml5, faCss3Alt } from "@fortawesome/free-brands-svg-icons";
import { Link } from "react-router-dom";






const getAllProjects = () => {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
  
    useEffect(() => {
      Axios.get("http://localhost:3000/projects.json")
        .then((response) => {
          setProjects(response.data);
          setLoading(false);
        })
        .catch((error) => {
          setError(error);
          setLoading(false);
        });
    }, []);
  
    return { projects, loading, error };
    };

 
       


    
   

const ProjectHoverCard = () => {

    const { projects, loading, error } = getAllProjects();
    const { id } = useParams();
    const project = projects.find((project) => project.id === parseInt(id));
    const { title, description, image, github, live, tech } = project;
    const techArray = tech.split(",");

    return (
        <div className="project-hover-card">
            <div className="project-hover-card__image">
                <img src={image} alt={title} />
            </div>
            <div className="project-hover-card__content">
                <h2>{title}</h2>
                <p>{description}</p>
                </div>
                </div>
    );
};

export default ProjectHoverCard;
