 // create a hero component that includes the header, about me, and button to view work. Then import the hero component into the landing page and render it.


 import React from 'react';
    import '../index.css';

    function Hero() {
        return (
            <div className='hero'>
            <header>
                <h1>Amol's Portfolio</h1>
                <img src={require("../src/assests/profilepic.png")} alt="Your Name" />
            </header>
            <h2>About Me</h2>
            <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam
                efficitur lorem id tellus ornare, ac laoreet erat porttitor. Morbi
                elementum consectetur mauris, eu fringilla nisi volutpat ac. Sed
                tincidunt erat ut lorem ornare, eget dictum justo rutrum.
            </p>
            {/* button to go to projects page */}
            <button><a href="#">View My Work</a></button>
            </div>
        );
        }

    export default Hero;
