class GitHubCards extends Component {
  Title = [];
  state = {
    repo: []

  };





  componentDidMount() {

    const api_key = process.env.REACT_APP_API_KEY;


  

  
    let repo = [ 'CMST488-AdvancedJS', 'Amol-React-Portfolio', 'PapaRebuild', 'CMST388', 'AmolPortfolio']
    repo.map((repo) => {
      Axios.get(`https://api.github.com/repos/amolpatil/${repo}`)
      .then((res) => {
        this.setState({
          repo: [...this.state.repo, res.data],
        });
      })
    }
    )

  }


  render() {
    const { repo } = this.state;
    return (
      <Grid container spacing={5} >
        {repo.map((data, i) => (
          <RepoCard repo={data} key={i}  />
        ))}
      </Grid>
    );

  }
}



export default GitHubCards;
